mapper.select:
SELECT adminreplyno, serviceno, adminno, adminreplytitle, adminreplycontent, adminreplyvisible, rdate
FROM admin_reply 
WHERE adminreplyno = #{adminreplyno}

mapper.update:
UPDATE admin_reply
SET adminreplytitle = #{adminreplytitle}, adminreplycontent=#{adminreplycontent}, adminreplyvisible=#{adminreplyvisible}, UDATE=sysdate,
    file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE adminreplyno = #{adminreplyno}

mapper.select:
    SELECT COUNT(*)
		FROM admin_reply
		WHERE serviceno = #{serviceno}
