mapper.select:
SELECT cateno, catename, rdate, udate, seqno, visible
FROM cate
WHERE cateno = #{cateno}

mapper.update:
UPDATE cate
SET catename=#{catename}, seqno=#{seqno}, udate=sysdate
WHERE cateno=#{cateno}

UPDATE cate
SET seqno = seqno - 1
WHERE cateno = #{cateno }

UPDATE cate
SET seqno = seqno + 1
WHERE cateno = #{cateno }

UPDATE cate
SET visible = 'Y'
WHERE cateno=#{cateno }

UPDATE cate
SET visible = 'N'
WHERE cateno=#{cateno }
