mapper.select:
SELECT COUNT(memberid) as cnt
FROM member
WHERE memberid=#{memberid}

SELECT memberno, memberid, memberpasswd, membername, tel, mdate
FROM member
WHERE memberno = #{memberno}

SELECT memberno, memberid, memberpasswd, membername, tel, mdate
FROM member
WHERE memberid = #{memberid}

mapper.update:
UPDATE member 
SET membername=#{membername}, tel=#{tel}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE memberno=#{memberno} AND memberpasswd=#{memberpasswd}

mapper.update:
UPDATE member
SET memberpasswd=#{memberpasswd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE memberid=#{memberid} AND memberpasswd=#{memberpasswd}

SELECT memberno, memberid, memberpasswd, membername, tel, mdate
FROM member
WHERE tel = #{tel}

SELECT memberid
FROM member
WHERE tel = #{tel}
